import React, {useEffect, useState} from 'react';
import {TbCircleDashed} from "react-icons/tb";
import {BiCommentDetail} from "react-icons/bi";
import {AiOutlineAudio, AiOutlineSearch} from "react-icons/ai";
import {BsEmojiSmile, BsFilter, BsThreeDotsVertical} from "react-icons/bs";
import ChatCard from "./chat/ChatCard";
import gangChat from 'C:\\Users\\yenic\\OneDrive\\Masaüstü\\react-app\\chat-app\\chat-app\\src\\Material\\images\\gangChat.png';
import MessageCard from "./message/MessageCard";
import {ImAttachment} from "react-icons/im";
import "./HomePage.css"
import {useNavigate} from "react-router-dom";
import Profile from "./profile/Profile";
import {Button, Menu, MenuItem} from "@mui/material";
import CreateGroup from "./group/CreateGroup";
import {useDispatch, useSelector} from "react-redux";
import {currentUser, logoutAction, searchUser} from "../Redux/Auth/Action";
import {createChat, getUsersChat} from "./chat/constants/Action";

const HomePage = () => {
    const [querys, setQuerys] = useState(null);
    const [currentChat, setCurrentChat]=useState(null);
    const [content, setContent] = useState("");
    const [isProfile, setIsProfile] = useState(false);
    const navigate = useNavigate();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const [isGroup, setIsGroup]= useState(false);
    const dispatch=useDispatch();
    const {auth, chat, message} = useSelector(store=>store);
    const token = localStorage.getItem("token")
    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
      setAnchorEl(null);
    };
    const handleClickOnChatCard=(userId)=>{
        //setCurrentChat(item)
        //console.log(userId, "-----------", item)
        dispatch(createChat({token, data:{userId}}))
        setQuerys("")
    }
    const handleSearch = (keyword)=> {
        dispatch(searchUser({keyword, token}))
    };
    const handleSendMessage=()=>{};

    useEffect(() => {
        dispatch(getUsersChat({token}))
    },[chat.createdChat, chat.createdGroup])
    const handleNavigate = ()=> {
        //navigate("/profile")
        setIsProfile(true);
    }
    const handleCloseProfile=()=>{
        setIsProfile(false);
    }

    const handleCreateGroup=()=>{
        setIsGroup(true)
    }

    const handleLogout=() => {
        dispatch(logoutAction())
    }

    useEffect(()=> {
        dispatch(currentUser(token))
    }, [token])

    useEffect(()=>{
        if(!auth.reqUser){
            navigate("/signup")
        }
    }, [auth.reqUser])

    const handleCurrentChat=(item)=>{
        setCurrentChat(item)
    }


    return (
        <div className='relative'>
            <div className='top-0 w-full py-14 bg-[#3A4750]'></div>
            <div className='flex bg-[#3A4750] h-[90vh] absolute left-[2vw] top-[5vh] w-[96vw]'>
                <div className='left w-[30%] bg-[#F6C90E] h-full]'>
                    {/*PROFILE*/}
                    {isGroup && <CreateGroup/>}
                    {isProfile && <div className='w-full h-full'><Profile handleCloseProfile={handleCloseProfile}/></div>}
                    {!isProfile && !isGroup && <div className='w-full'>
                    {/*HOME*/}
                        <div className="flex justify-between items-center p-3">
                            <div onClick={handleNavigate} className="flex items-center space-x-3">
                                <img className='rounded-full w-10 h-10 cursor-pointer' src="data:image/png;base64," alt=""/>
                                <p>                                        {auth.reqUser?.firstName +" "+ auth.reqUser?.lastName }
                                </p>
                            </div>
                            <div className="space-x-3 text-2xl flex">
                                <TbCircleDashed className='cursor-pointer' onClick={()=>navigate("/status")}/>
                                <BiCommentDetail/>
                                <div>
                                      <BsThreeDotsVertical  className='cursor-pointer'  id="basic-button"
                                                             aria-controls={open ? 'basic-menu' : undefined}
                                                             aria-haspopup="true"
                                                             aria-expanded={open ? 'true' : undefined}
                                                             onClick={handleClick}/>
                                      <Menu
                                          id="basic-menu"
                                          anchorEl={anchorEl}
                                          open={open}
                                          onClose={handleClose}
                                          MenuListProps={{
                                            'aria-labelledby': 'basic-button',
                                          }}
                                        >
                                          <MenuItem onClick={handleCreateGroup}>Hesabım</MenuItem>
                                          <MenuItem onClick={handleCreateGroup}>Grup Oluştur</MenuItem>
                                          <MenuItem onClick={handleLogout}>Çıkış</MenuItem>
                                        </Menu>
                                </div>
                            </div>
                        </div>
                        <div className='relative flex justify-center items-center bg-white py-4 px-3'>
                            <input className='border-none outline-none bg-slate-200 rounded-md w-[93%] pl-9 py-2'
                                   type='text'
                                   placeholder='Search or start new Chat'
                                   onChange={(e)=>{
                                       setQuerys(e.target.value)
                                       handleSearch(e.target.value)
                                   }}
                                   value={querys}
                            />
                            <AiOutlineSearch className='left-5 top-7 absolute'/>
                            <div>
                                <BsFilter className='ml-4 text-3xl'/>
                            </div>
                        </div>
                        {/*ALL USER*/}
                        <div className='bg-white overflow-y-scroll h-[76.8vh] px-3'>
                            {querys &&
                                auth.searchUser?.map((item)=><div onClick={()=> handleClickOnChatCard(item.id)}>
                                    <hr/>
                                    <ChatCard
                                        name={item.username}
                                        userImg={
                                            item.profilePictureUrl || "data:image/jpeg;base64,"
                                        }
                                    />
                                </div>)}
                            {chat.chats!==null && !querys &&
                                chat.chats?.map((item)=><div onClick={()=> handleCurrentChat(item)}>
                                    <hr/>
                                    {item.is_group ? (
                                        <ChatCard
                                            name={item.name}
                                            userImg={
                                                item.chat_image || "data:image/jpeg;base64,"
                                            }
                                        />
                                    ):(
                                        <ChatCard
                                            isChat={true}
                                            name={
                                                auth.reqUser?.id !== item.users[0]?.id
                                                    ? item.users[0].username
                                                    : item.users[1].username
                                            }

                                            userImg={
                                                auth.reqUser.id !== item.users[0].id
                                                    ? item.users[0].profile_picture ||
                                                      "data:image/jpeg;base64,"
                                                    : item.users[1].profile_picture ||
                                                      "data:image/jpeg;base64,"
                                            }
                                            //notification={notification.length}
                                            //isNotification={
                                            //    notification[0]?.chat?.id === item.id
                                            //}
                                            //message={
                                            //    (item.id ===
                                            //        messages[messages.length - 1]?.chat?.id &&
                                            //        messages[messages.length - 1]?.content) ||
                                            //    (item.id === notifications[0]?.chat?.id &&
                                            //        notifications[0]?.content
                                            //    )
                                            //}
                                        />
                                    )}
                                </div>)}
                        </div>
                        <div>
                            {/*Default whatsUpChat*/}
                        </div>
                    </div>}
                </div>
                {!currentChat &&
                    <div className='w-[70%] flex flex-col items-center justify-center h-full'>
                        <div className='max-w-[50%] text-center'>
                            <img src={gangChat} alt=''/>
                            <h1 className='text-4xl text-[#F6C90E]'>GANG CHAT</h1>
                            <p className='my-9 text-[#F6C90E]'>Gang up with your friend right now.</p>
                        </div>
                    </div>
                }
                {/*Message Part*/}
                {currentChat &&
                    <div className='w-[70%] relative'>
                        <div className='header absolute top-0 w-full bg-[#f0f2f5]'>
                            <div className='flex justify-between'>
                                <div className='py-3 space-x-4 flex items-center px-3'>
                                    <img className='w-10 h-10 rounded-full'
                                         src={auth.reqUser.id !== currentChat.users[0].id
                                                    ? currentChat.users[0].profile_picture ||
                                                      "data:image/jpeg;base64,"
                                                    : currentChat.users[1].profile_picture ||
                                                      "data:image/jpeg;base64,"} alt=''></img>
                                    <p>
                                        {currentChat.is_group? currentChat.name:(auth.reqUser?.id ===currentChat.users[0].id?currentChat.users[1].username:currentChat.user[0].username)}
                                    </p>
                                </div>
                                <div className='py-3 flex space-x-4 items-center px-3'>
                                    <AiOutlineSearch/>
                                    <BsThreeDotsVertical/>
                                </div>
                            </div>
                        </div>
                        {/*MessageSection*/}
                        <div className='px-10 h-[85vh] overflow-y-scroll'>
                            <div className='space-y-1 flex flex-col justify-center mt-20 py-2'>
                                {[1,1,1,1,1,1,1].map((item, i)=> <MessageCard isMessage={i%2===0} content={"message"}/>)}
                            </div>
                        </div>
                        {/* footer part*/}
                        <div className='flex items-center justify-between p-4 bg-[#f0f2f5]'>
                            <div className='flex items-center space-x-4'>
                                <BsEmojiSmile className='cursor-pointer text-2xl text-gray-500'/>
                                <ImAttachment className='cursor-pointer text-2xl text-gray-500' />
                            </div>
                            <input
                                className='ml-4 flex-1 py-2 px-4 outline-none border-none bg-white rounded-md shadow-sm text-gray-700'
                                type='text'
                                onChange={(e) => setContent(e.target.value)}
                                placeholder='Type message'
                                value={content}
                                onKeyDown={(e) => {
                                  if (e.key === 'Enter') {
                                    handleSendMessage();
                                    setContent('');
                                  }
                                }}
                              />
                              <div className="ml-4">
                                  <AiOutlineAudio className="cursor-pointer text-2xl text-gray-500" />
                                </div>

                        </div>
                    </div>
                   }

            </div>
        </div>
    );
};

export default HomePage;